import React from "react"

import WorkIcon from "mdi-react/WorkIcon"
import SchoolIcon from"mdi-react/SchoolIcon"
import StarIcon from "mdi-react/StarIcon"

import { VerticalTimeline }  from 'react-vertical-timeline-component';
// import 'react-vertical-timeline-component/style.min.css';

import TimelineElement from "../TimelineElement/TimelineElement"

import aboutmeData from "./data/aboutmeData"

import "./AboutMe.css"

function getIcon(icon){
	if(icon === "StarIcon")
		return <StarIcon />
	else if(icon === "WorkIcon")
		return <WorkIcon />
	else if(icon === "SchoolIcon")
		return <SchoolIcon />
	else 
		return <StarIcon />
}

function AboutMe(){
    const iconStyle = {
		background: "rgb(100, 100, 100)",
		color: "white"
    }
    
    const myAboutmeData = aboutmeData.map(function(data){
		const icon = getIcon(data.icon)
		return <TimelineElement key={data.id} data={data} icon={icon} iconStyle={iconStyle}/>
    })

    return(
        // <div className="aboutme__body">
        //     <div className="aboutme__header">
        //         <div className="aboutme__image"></div>
        //     </div>
        // </div>
        <div className="aboutme__body">
            <VerticalTimeline>
                {myAboutmeData}
            </VerticalTimeline>
        </div>
    )
}

export default AboutMe
