import React from "react"

import "./AboutMe.css"

import WorkIcon from "mdi-react/WorkIcon"
import SchoolIcon from"mdi-react/SchoolIcon"
import StarIcon from "mdi-react/StarIcon"

import { VerticalTimeline }  from 'react-vertical-timeline-component';
import 'react-vertical-timeline-component/style.min.css';

import TimelineElement from "../TimelineElement/TimelineElement"
import aboutmeData from "./data/aboutmeData"

function getIcon(icon){
	if(icon === "StarIcon")
		return <StarIcon />
	else if(icon === "WorkIcon")
		return <WorkIcon />
	else if(icon === "SchoolIcon")
		return <SchoolIcon />
	else 
		return <StarIcon />
}


function AboutMe(){
  const iconStyle = {
		background: "rgb(100, 100, 100)",
		color: "white"
    }
    
    const myAboutmeData = aboutmeData.map(function(data){
		const icon = getIcon(data.icon)
		return <TimelineElement key={data.id} data={data} icon={icon} iconStyle={iconStyle}/>
    })

    return(
        <div className="aboutme__body">
          {/* <VerticalTimeline>
            {myAboutmeData}
          </VerticalTimeline> */}
          <div class="container-fluid">
            <div class="row example-basic">
                <div class="col-md-12 example-title">
                    <h2>Basic Timeline</h2>
                    <p>Extra small devices (phones, less than 768px)</p>
                </div>
                <div class="col-xs-10 col-xs-offset-1 col-sm-8 col-sm-offset-2">
                    <ul class="timeline">
                        <li class="timeline-item">
                            <div class="timeline-info">
                                <span>March 12, 2016</span>
                            </div>
                            <div class="timeline-marker"></div>
                            <div class="timeline-content">
                                <h3 class="timeline-title">Event Title</h3>
                                <p>Nullam vel sem. Nullam vel sem. Integer ante arcu, accumsan a, consectetuer eget, posuere ut, mauris. Donec orci lectus, aliquam ut, faucibus non, euismod id, nulla. Donec vitae sapien ut libero venenatis faucibus. ullam dictum felis
                                    eu pede mollis pretium. Pellentesque ut neque.</p>
                            </div>
                        </li>
                    </ul>
                </div>
            </div>
        </div>
        </div>
    )
}

export default AboutMe
