import React from "react"

import boxlinkData from "../Startseite/data/boxlinkData"
import MenuItems from "../MenuItems/MenuItems"

import XingIcon from "mdi-react/XingIcon"
import LinkedinIcon from "mdi-react/LinkedinIcon"
import GithubIcon from "mdi-react/GithubIcon"
import HackerRankIcon from "mdi-react/HackernewsIcon"

import "./SideDrawer.css"

function SideDrawer(props){
    let drawerClasses = 'side-drawer'
    if(props.show)
        drawerClasses = 'side-drawer open'

    const myMenuItems = boxlinkData.map(function(item){
        return (
            <MenuItems key={item.id} title={item.case}/>
        )
    })

    return(
        <nav className={drawerClasses}>
            <ul>
                {myMenuItems}
            </ul>
            <XingIcon />
        </nav>
    )
}

export default SideDrawer
