import React from "react"

function getProjectImage(img){
    console.log("IMAGE: " + img)
    switch(img){
        case "TradedexLogo.png":
            return(
                <div id="icon_tradedex_container" class="animation_zoom"></div>
            )
        case "KD":
            return(
                <div id="icon_kd_container" class="animation_zoom"> <span id="icon_KD">KD</span> </div>
            )
        case "SitKrit.png":
            return(
                <img src={require("../portfolio_assets/SitKrit.png")} alt="img" className="icon_left"></img>
            )
        case "KhangDinh.jpg":
            return(
                <img src={require("../portfolio_assets/BildKhang.jpg")} alt="img" className="icon_right"></img>
            )
    }
}

function Project(props){
    const skills = props.project.tools.map(function(skill){
        return <li key={skill}>
            {skill}
        </li>
    });

    if(props.img_pos == "l"){
        const image = require("../portfolio_assets/SitKrit.png")
        return(
            <div className="container_gray">
                {/* <img src={image} alt="img" className="icon_left"></img> */}
                <getProjectImage props={props.project.img}/>
                <article>
                    <h2 className="h2_left">{props.project.caption}</h2>
                    <p className="p_left">{props.project.text}</p>
                    <br></br>
                    <p className="p_left" style={{display: props.project.url_android ? "block" : "none"}}>
                        Android: <a href={props.project.url_android} className="tradedex_link">Link</a>
                        <br></br>
                        iOS: <a href={props.project.url_ios} className="tradedex_link">Link</a>
                        <br></br><br></br>
                    </p>
                    <ul className="ul_left">
                        {skills}
                    </ul>
                </article>
                <p></p>
            </div>
        )
    }
    else{
        const image = require("../portfolio_assets/BildKhang.jpg")
        return(
            <div className="container_white">
                <p></p>
                <article>
                    <h2 className="h2_right">{props.project.caption}</h2>
                    <p className="p_right">{props.project.text}</p>
                    <br></br>
                    <ul className="ul_right">
                        {skills}
                    </ul>
                </article>
                <img src={image} alt="img" className="icon_right"></img>
            </div>
        )
    }
}

export default Project
