import React from "react"

function ProjectImage(props){
    console.log("IMAGE: " + props.img)
    if(props.img === "SitKrit.png")
        return(<img src={require("../portfolio_assets/SitKrit.png")} alt="img" className="icon_left"></img>)
    else if(props.img === "BildKhang.jpg")
        return(<img src={require("../portfolio_assets/BildKhang.jpg")} alt="img" className="icon_left"></img>)
    else if(props.img === "TradedexLogo.png")
        return(<div id="icon_tradedex_container" className="animation_zoom"></div>)
    else if(props.img === "KD.png")
        return(<div id="icon_kd_container" className="animation_zoom"> <span id="icon_KD">KD</span> </div>)
    else{
        return (<div></div>)
    }
}

function Project(props){
    const skills = props.project.tools.map(function(skill){
        return <li key={skill}>
            {skill}
        </li>
    });

    // ProjectImage(props.project.img)

    const image = require("../portfolio_assets/SitKrit.png")

    if(props.img_pos === "l"){
        return(
            <div className="container_gray">
                {/* <img src={image} alt="img" className="icon_left"></img> */}
                <ProjectImage img={props.project.img}/>
                <article>
                    <h2 className="h2_left">{props.project.caption}</h2>
                    <p className="p_left">{props.project.text}</p>
                    <br></br>
                    <p className="p_left" style={{display: props.project.url_android ? "block" : "none"}}>
                        Android: <a href={props.project.url_android} className="tradedex_link">Link</a>
                        <br></br>
                        iOS: <a href={props.project.url_ios} className="tradedex_link">Link</a>
                        <br></br><br></br>
                    </p>
                    <ul className="ul_left">
                        {skills}
                    </ul>
                </article>
                <p></p>
            </div>
        )
    }
    else{
        return(
            <div className="container_white">
                <p></p>
                <article>
                    <h2 className="h2_right">{props.project.caption}</h2>
                    <p className="p_right">{props.project.text}</p>
                    <br></br>
                    <ul className="ul_right">
                        {skills}
                    </ul>
                </article>
                <ProjectImage img={props.project.img}/>
                {/* <img src={image} alt="img" className="icon_right"></img> */}
            </div>
        )
    }
}

export default Project
