import React from "react"
import WorkIcon from "mdi-react/WorkIcon"
import SchoolIcon from"mdi-react/SchoolIcon"
import StarIcon from "mdi-react/StarIcon"

import { VerticalTimeline, VerticalTimelineElement }  from 'react-vertical-timeline-component';
import 'react-vertical-timeline-component/style.min.css';

import TimelineElement from "./aboutme_components/TimelineElement"
import aboutmeData from "./aboutme_data/AboutmeData"

// ------------------ react-vertical-timeline-component ------------------
// source: https://www.npmjs.com/package/react-vertical-timeline-component

function getIcon(icon){
	if(icon === "StarIcon")
		return <StarIcon />
	else if(icon === "WorkIcon")
		return <WorkIcon />
	else if(icon === "SchoolIcon")
		return <SchoolIcon />
	else 
		return <StarIcon />
}

function AboutMe(){
	const iconStyle = {
		background: "rgb(100, 100, 100)",
		color: "white"
	}

	const myAboutmeData = aboutmeData.map(function(data){
		const icon = getIcon(data.icon)
		return <TimelineElement key={data.id} data={data} icon={icon} iconStyle={iconStyle}/>
	})

    document.body.style.backgroundColor = "rgb(230, 230, 230)"
    return(
        <div className="container_main">
            <VerticalTimeline>
				{myAboutmeData}
			</VerticalTimeline>
        </div>
    )
}

export default AboutMe
