import React from "react"
import {BrowserRouter as Router,Switch, Route, Link} from "react-router-dom";

import "./MenuItems.css"

import Startseite from "../Startseite/Startseite"

function getMenuLink(menuCase){
    if(menuCase === "Startseite")
        return '/'
    else if(menuCase === "Über Mich")
        return '/AboutMe'
    else
        return '/' + menuCase
}

function MenuItems(props){
    const menuLink = getMenuLink(props.title)

    return(
        <Router>
            <li><Link to={menuLink}>{props.title}</Link></li>
            <Switch>
                <Route exact path="/">
                    <Startseite />
                </Route>
            </Switch>
        </Router>
        // <li><a href="/" className="menu-items__item">{props.title}</a></li>
        // <li><Link></Link></li>
    )
}

export default MenuItems
