import React from "react"

import "./Project.css"

function Project(props){
    const skills = props.project.tools.map(function(skill){
        return <li key={skill}> {skill} </li>
    });

    let sectionClass
    if(props.img_pos === "l"){
        sectionClass = "project__container gray"
        document.getElementById("img_pos_r").style.color = "red"
    }
    else{
        sectionClass = "project__container white"
        document.getElementById("img_pos_l").style.color = "blue"
    }

    return(
        <section className={sectionClass}>
            <div className="project__image" id="img_pos_l"></div>
            <article>
                <h2>{props.project.caption}</h2>
                <p>{props.project.text}</p>
                <br />
                <p style={{display: props.project.url_android ? "block" : "none"}}>
                    Android: <a href={props.project.url_android}>Link</a>
                    <br /> 
                    iOS: <a href={props.project.url_ios}>Link</a>
                    <br /> <br /> 
                </p>
                <ul className="ul_left">
                    {skills}
                </ul>
            </article>
            {/* <div className="project__image" id="img_pos_r"></div> */}
        </section>
    )

